<script>
  import { tick } from 'svelte';
  import Ui from './Ui.svelte';

  let time = 0;
  let duration;
  let showControlsTimeout;
  let className = '';
  let hydrated = false;
  let hydrating = false;
  let currentPoster;
  let buffered;
  let playPromise;
  let _src;
  let allowScrubbing = false;
  let target;
  let hideControlsOnPause = true;
  let customControls;

  export let video = null;
  export let src;
  export let autoplay;
  export let muted;
  export let poster;
  export let type;
  export let controls;
  export let paused = true;
  export let preload = 'none';
  export { className as class };

  $: customControls = !controls;
  $: ((p) => {
    if (currentPoster !== p) {
      currentPoster = p;
      reset();
    }
  })(poster);
  $: showControls = paused;
  $: paused && !hideControlsOnPause && clearTimeout(showControlsTimeout);

  function handleMouseenter(e) {
    e = e.detail;
    showControls = true;
  }

  function handleMouseleave(e) {
    e = e.detail;
    showMediaControls();
  }

  function showMediaControls() {
    if (!duration) return; // nothing loaded
    clearTimeout(showControlsTimeout);
    showControlsTimeout = setTimeout(() => (showControls = false), 4000);
    showControls = true;
  }

  function handleMousemove(e) {
    e = e.detail;
    showMediaControls();
    allowScrubbing && handleScrubbing(e);
  }

  function handleClearTimeout(e) {
    clearTimeout(showControlsTimeout);
  }

  function handleScrubbing(e) {
    if (!(e.buttons & 1)) return; // mouse not down
    if (!duration) return; // video not loaded yet

    const { left, right } = video && video.getBoundingClientRect();
    if (left === undefined || right === undefined) return;

    time = (duration * (e.clientX - left)) / (right - left);
    scrubbing = true;
  }

  function handlePlayPause(e) {
    // we have switched off preload, so must load first
    !duration && !playPromise && video.load();
    playPromise = video.play();
    if (playPromise && !paused) {
      playPromise
        .then((_) => {
          // playback started so we can safely pause
          video.pause();
        })
        .catch((_) => {});
    }
  }

  function handleRewind(e) {
    let step = e.detail || 15;
    let s;
    time -= (s = time - step) < 0 ? step + s : step;
  }

  function handleForeward(e) {
    let step = e.detail || 15;
    time += time + step > duration ? duration - time : step;
  }

  function handleMousedown(e) {
    e = e.detail;
    target = e.currentTarget;

    // we can't rely on the built-in click event, because it fires
    // after a drag â€” we have to listen for clicks ourselves

    function handleMouseup() {
      _src && (src = _src);
      handlePlayPause();
      cancel();
    }

    function handleMouseupAfterDrag() {
      scrubbing = false;
      cancelAfterDrag();
    }

    function cancel() {
      target.removeEventListener('mouseup', handleMouseup);
      target.addEventListener('mouseup', handleMouseupAfterDrag);
      target.addEventListener('mouseleave', handleMouseupAfterDrag);
    }

    function cancelAfterDrag() {
      target.removeEventListener('mouseup', handleMouseupAfterDrag);
      target.removeEventListener('mouseleave', handleMouseupAfterDrag);
    }

    target.addEventListener('mouseup', handleMouseup);

    setTimeout(cancel, 100); // prevent start/stop after scrubbing
  }

  function handleCanplay(e) {}

  function handleLoadstart(e) {
    hydrating = true;
    hydrated = false;
  }

  function handleLoadedData(e) {
    hydrating = false;
    hydrated = true;
  }

  function handleEmptied(e) {
    hydrated = false;
  }

  function handleAborted(e) {
    duration = undefined;
  }

  function handlePictureInPicture(e) {
    if (document.pictureInPictureElement) document.exitPictureInPicture().catch((e) => {});
    else video && video.requestPictureInPicture().catch((e) => {});
  }

  function handleFullscreen(e) {
    if (document.fullscreenElement) document.exitFullscreen().catch((e) => {});
    else if (video && video.requestFullscreen) video.requestFullscreen();
    else if (video && video.webkitRequestFullScreen) video.webkitRequestFullScreen();
  }

  async function reset() {
    if (!duration) return;
    video.pause();
    await tick();
    setTimeout(() => video.load(), 100);
  }
</script>

<div class="player {className}" class:hydrated>
  <video
    {poster}
    {preload}
    {controls}
    {muted}
    {type}
    {autoplay}
    on:loadstart={handleLoadstart}
    on:canplay={handleCanplay}
    on:loadeddata={handleLoadedData}
    on:emptied={handleEmptied}
    on:abort={handleAborted}
    bind:currentTime={time}
    bind:duration
    bind:paused
    bind:buffered
    bind:this={video}
    x-webkit-airplay="allow"
  >
    <source {src} type="video/{type}" />
    <track kind="captions" />
    Your browser does not support the
    <code>video</code>
    element.
  </video>
  {#if customControls}
    <Ui
      on:mousemove={handleMousemove}
      on:mouseenter={handleMouseenter}
      on:mouseleave={handleMouseleave}
      on:clearTimeout={handleClearTimeout}
      on:fullscreen={handleFullscreen}
      on:play-pause={handlePlayPause}
      on:rwd={handleRewind}
      on:fwd={handleForeward}
      on:pip={handlePictureInPicture}
      bind:time
      {duration}
      {showControls}
      {paused}
      {buffered}
    />
  {/if}
</div>

<style>
  .player {
    position: relative;
    user-select: none;
  }

  video {
    width: 100%;
    width: var(--player-w);
    object-position: center;
    object-fit: cover;
  }

  .hydrated video {
    object-fit: contain;
  }
</style>
