<script>
  import './chip.scss';
  import { stores } from '@sapper/app';
  import { warnings } from '../../stores/warningStore';
  import Chip, { Icon, Text } from '@smui/chips';

  export let selectionUserId;
  export { className as class };

  let className = '';

  $: userWarnings = ($warnings.has(selectionUserId) && $warnings.get(selectionUserId).warnings) || [];
</script>

<div class="chips">
  {#each userWarnings as warning}
    <div class="chip">
      <Chip class="warning {className}">
        <Icon class="material-icons" leading>circle_notifications</Icon>
        <Text>{warning.label}</Text>
      </Chip>
    </div>
  {/each}
</div>

<style>
  .chips {
    display: inline-flex;
    flex-wrap: wrap;
  }
  .chip {
    margin-right: 5px;
  }
</style>
