import { writable } from 'svelte/store';

function createStore() {
  const defaults = {
    timeout: 4000,
    permanent: false,
    type: 'success',
    message: ''
  };
  const { subscribe, update, set } = writable(defaults, () => {});

  let { permanent, timeout, type } = defaults;
  let timeoutId;

  return {
    subscribe,
    update: (value) =>
      update(() => {
        ({ permanent, timeout } = { ...defaults, ...value });
        clearTimeout(timeoutId);
        if (!permanent === true) {
          timeoutId = setTimeout((val) => set(val), timeout, { ...defaults });
        }
        return { ...defaults, ...value, permanent };
      }),
    text: (message) => update((val) => ({ ...val, message }))
  };
}

export default createStore();
