import { fromUnixTime, compareAsc } from 'date-fns';
import { formatISO9075 } from 'date-fns';

export function get(endpoint) {
  return fetch(endpoint, {
    method: 'GET',
    credentials: 'include',
    headers: {
      'Content-Type': 'application/json',
    },
  }).then((r) => r.json());
}
export function post(endpoint, data = {}) {
  return fetch(endpoint, {
    method: 'POST',
    credentials: 'include',
    body: JSON.stringify(data),
    headers: {
      'Content-Type': 'application/json',
    },
  }).then((r) => r.json());
}
export function del(endpoint, id) {
  return fetch(endpoint, {
    method: 'DELETE',
    credentials: 'include',
    body: id,
    headers: {
      'Content-Type': 'application/json',
    },
  }).then((r) => r.json());
}
export function put(endpoint, data = {}) {
  return fetch(endpoint, {
    method: 'PUT',
    credentials: 'include',
    body: JSON.stringify(data),
    headers: {
      'Content-Type': 'application/json',
    },
  }).then((r) => r.json());
}
export function equals(obj_1, obj_2) {
  let json_1 = typeof obj_1 === 'object' && JSON.stringify(obj_1);
  let json_2 = typeof obj_2 === 'object' && JSON.stringify(obj_2);
  if (!!json_1 && !!json_2) {
    return json_1 === json_2;
  }
  return false;
}
export function toLocalDate(date, locale = "de-DE", options = {}) {
  let defaults = {
    year: false,
    month: false, // 2-digit
    day: false,
    hour: false,
    minute: false,
  };
  options = { ...defaults, ...options };
  return new Date(date).toLocaleDateString('de-DE', options);
}
export function toLocalTime(date, locale = "de-DE", options = {}) {
  let defaults = {
    year: false,
    month: false, // 2-digit
    day: false,
    hour: false,
    minute: false,
  };
  options = { ...defaults, ...options };
  return new Date(date).toLocaleDateString('de-DE', options);
}
export function minDigits(m) {
  toLocaleString('en-US', {
    //this is the function that formats the numbers
    minimumIntegerDigits: 2, //change this to your minimum length
    useGrouping: false,
  });
}
export function formatter(d) {
  let h, hrs, m, min, s, sec;
  hrs = d / 1000 / 60 / 60;
  min = !isNaN(((hrs % Math.floor(hrs)) * 60) % 60) || 0;
  sec = !isNaN(((min % Math.floor(min)) * 60) % 60) || 0;
  return `${Math.floor(hrs)}:${Math.floor(min)}:${Math.floor(sec)}`;
}
export function isExpired(endDate) {
  if (!(endDate instanceof Date)) endDate = fromUnixTime(endDate);
  return compareAsc(new Date(), endDate) !== -1 ? true : false;
}
export function hasStarted(date) {
  return compareAsc(new Date(), date) !== -1 ? true : false;
}
export function createRedirectSlug(page) {
  let queries = Object.entries(page.query)
    .map(([key, val]) => `${key}=${val}`)
    .join('&');
  let bit = '';
  return (bit = `?redirect=${page.path}`) && queries ? `${bit}&${queries}` : bit;
}
export function redirectPath(page, session) {
  let path, tab;

  path = session.role === 'Administrator' ? 'users' : 'videos';
  if (session.role !== 'Administrator') return path;
  return page.query.redirect && (path = page.query.redirect)
    ? (tab = page.query.tab)
      ? path + `?tab=${tab}`
      : path
    : path;
}
export function toISODate(date, tillMidnight) {
  return formatISO9075(
    new Date(
      date.getFullYear(),
      date.getMonth(),
      date.getDate(),
      tillMidnight ? 23 : date.getHours(),
      tillMidnight ? 59 : date.getMinutes(),
      tillMidnight ? 59 : date.getSeconds()
    )
  );
}
export function windowSize() {
  var width = 0,
    height = 0;
  if (document.documentElement && (document.documentElement.clientWidth || document.documentElement.clientHeight)) {
    width = document.documentElement.clientWidth;
    height = document.documentElement.clientHeight;
  } else {
    if ('number' == typeof window.innerWidth) {
      width = window.innerWidth;
      height = window.innerHeight;
    } else if (document.body && (document.body.clientWidth || document.body.clientHeight)) {
      width = document.body.clientWidth;
      height = document.body.clientHeight;
    }
  }
  return {
    height: height,
    width: width,
  };
}
